%Dhrona%Question listtryload('question.txt');catchquestion=cell(1000,4);% question format - question,answer choices,correct answer,âˆžlevel%% question{1,1}='What is 100*100?';% question{1,2}={'100','1000','10000','100000'};% question{1,3}='10000';% question{1,4}=3;%question generatorfor i=1:1000q1=randi([0,104]);q2=randi([0,200]);%question text generationq3=strcat({'What is'},{' '},num2str(q1),{' * '},num2str(q2),{' ?'});q4=q1*q2;q5=(q1+randi([1,10]))*q2;  % Random wrong answerq6=(q1+randi([1,10]))*q2;  % Random wrong answerq7=(q1+randi([1,10]))*q2;  % Random wrong answer% Randomizing optionsq8=[q4,q5,q6,q7];ind = randperm (numel (q8));q9=q8(ind);q10=num2cell(q9);% choicesq11=num2str(q4);% Actual answer% getting number of digits in question to assign levelq12=numel(num2str(q1));q13=numel(num2str(q2));q14=q12+q13-1; % levelquestion{i,1}=q3;question{i,2}=q10;question{i,3}=q11;question{i,4}=q14;pth=pwd;save ([pth "/question.txt"], "question");endend_try_catch% Ask questionaskedq=[];%findind min level min_lvl=min(cell2mat(question(:,4)));askquestion(askedq,question,min_lvl);